import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import toast from "react-hot-toast";
import { API_BASE } from "../config";

export default function Channels() {
  const { user, logout } = useAuth();
  const [channels, setChannels] = useState([]);
  const [newChannelName, setNewChannelName] = useState("");

  const fetchChannels = async () => {
    try {
      const res = await axios.get(`${API_BASE}/channels`, { withCredentials: true });
      setChannels(res.data);
    } catch {
      toast.error("Failed to load channels");
    }
  };

  useEffect(() => {
    fetchChannels();
  }, []);

  const createChannel = async () => {
    if (!newChannelName.trim()) return;
    try {
      await axios.post(
        `${API_BASE}/channels`,
        { name: newChannelName },
        { withCredentials: true }
      );
      setNewChannelName("");
      fetchChannels();
      toast.success("Channel created");
    } catch (e) {
      toast.error(e.response?.data?.message || "Create failed");
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-4 bg-white rounded shadow">
      <header className="flex justify-between items-center mb-4">
        <h1 className="text-xl font-bold">Welcome, {user?.name}</h1>
        <button
          onClick={logout}
          className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700"
        >
          Logout
        </button>
      </header>
      <div className="mb-4">
        <input
          type="text"
          placeholder="New channel name"
          value={newChannelName}
          onChange={(e) => setNewChannelName(e.target.value)}
          className="border p-2 rounded w-full"
        />
        <button
          onClick={createChannel}
          disabled={!newChannelName.trim()}
          className="mt-2 w-full py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:bg-gray-400"
        >
          Create Channel
        </button>
      </div>
      <section>
        <h2 className="text-lg font-semibold mb-2">Channels</h2>
        {channels.length === 0 && <p>No channels found.</p>}
        <ul>
          {channels.map((channel) => (
            <li key={channel._id} className="border-b py-2">
              {channel.name} ({channel.members.length} members)
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
