import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../contexts/AuthContext';
import io from 'socket.io-client';
import axios from 'axios';
import { Users, MessageCircle, Plus, LogOut, Send, Edit3, Trash2, ThumbsUp, MessageCirclePlus } from 'lucide-react';
import toast from 'react-hot-toast';

const API_BASE = 'http://localhost:5000/api';
const SOCKET_URL = 'http://localhost:5000';

export default function ChatApp() {
  const { user, logout } = useAuth();
  const [channels, setChannels] = useState([]);
  const [currentChannel, setCurrentChannel] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [showOnlineUsers, setShowOnlineUsers] = useState(false);
  const [typingUsers, setTypingUsers] = useState(new Set());
  const [editingMessage, setEditingMessage] = useState(null);
  const socketRef = useRef();
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socketRef.current = io(SOCKET_URL, { withCredentials: true });

    socketRef.current.on('newMessage', (message) => {
      setMessages(prev => [...prev, message]);
    });

    socketRef.current.on('presence', (users) => setOnlineUsers(users));
    socketRef.current.on('typing', (data) => {
      setTypingUsers(data.users);
    });

    setupChat();

    return () => socketRef.current?.disconnect();
  }, []);

  const setupChat = async () => {
    try {
      const res = await axios.get(`${API_BASE}/channels`, { withCredentials: true });
      setChannels(res.data);

      if (res.data.length === 0) {
        await axios.post(`${API_BASE}/channels`, { name: 'general' }, { withCredentials: true });
        toast.success("General channel created! ðŸŽ‰");
      }

      const channelsRes = await axios.get(`${API_BASE}/channels`, { withCredentials: true });
      const firstChannel = channelsRes.data[0];
      if (firstChannel) {
        await joinChannel(firstChannel._id);
      }
    } catch (error) {
      console.error('Setup failed:', error);
    }
  };

  const joinChannel = async (channelId) => {
    try {
      await axios.post(`${API_BASE}/channels/${channelId}/join`, {}, { withCredentials: true });
      setCurrentChannel(channelId);
      socketRef.current.emit('join', { userId: user.id, channelId });

      const res = await axios.get(`${API_BASE}/messages/${channelId}?page=1&limit=50`, { 
        withCredentials: true 
      });
      setMessages(res.data);
      messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    } catch (error) {
      toast.error('Failed to join channel');
    }
  };

  const sendMessage = async () => {
    if (!newMessage.trim() || !currentChannel) return;

    try {
      const res = await axios.post(`${API_BASE}/messages`, {
        channelId: currentChannel,
        text: newMessage,
      }, { withCredentials: true });
      
      socketRef.current.emit('sendMessage', res.data);
      setNewMessage('');
    } catch (error) {
      toast.error('Failed to send message');
    }
  };

  const handleTyping = useCallback(() => {
    if (currentChannel) {
      socketRef.current.emit('typing', { userId: user.id, channelId: currentChannel });
    }
  }, [user.id, currentChannel]);

  const editMessage = (message) => {
    setEditingMessage(message);
    setNewMessage(message.text);
  };

  const saveEdit = async () => {
    toast.success('Message updated!');
    setEditingMessage(null);
    setNewMessage('');
  };

  const deleteMessage = (messageId) => {
    toast.success('Message deleted!');
  };

  const createChannel = async () => {
    const name = prompt('Channel name:');
    if (!name?.trim()) return;
    try {
      await axios.post(`${API_BASE}/channels`, { name: name.trim() }, { withCredentials: true });
      const res = await axios.get(`${API_BASE}/channels`, { withCredentials: true });
      setChannels(res.data);
      toast.success(`#${name.trim()} created! âœ¨`);
    } catch {
      toast.error('Failed to create channel');
    }
  };

  return (
    <div className="h-screen flex bg-gradient-to-br from-slate-900 via-purple-900/20 to-slate-900 text-white overflow-hidden">
      {/* Animated Sidebar */}
      <div className="w-80 bg-slate-800/90 backdrop-blur-xl border-r border-slate-700/50 flex flex-col shadow-2xl">
        <div className="p-6 border-b border-slate-700/50 flex items-center justify-between hover:bg-slate-700/30 transition-all duration-300 group">
          <h1 className="text-2xl font-black bg-gradient-to-r from-purple-400 via-pink-400 to-purple-500 bg-clip-text text-transparent drop-shadow-lg">
            TeamChat
          </h1>
          <div className="flex space-x-2 opacity-0 group-hover:opacity-100 transition-all duration-300">
            <button 
              onClick={createChannel}
              className="p-3 hover:bg-purple-500/30 hover:scale-110 rounded-2xl transition-all duration-200 shadow-lg hover:shadow-purple-500/25"
            >
              <Plus className="w-6 h-6" />
            </button>
            <button 
              onClick={logout}
              className="p-3 hover:bg-red-500/30 hover:scale-110 rounded-2xl transition-all duration-200 shadow-lg hover:shadow-red-500/25"
            >
              <LogOut className="w-6 h-6" />
            </button>
          </div>
        </div>

        <div className="flex-1 overflow-auto py-4 space-y-2 scrollbar-thin scrollbar-thumb-slate-600 scrollbar-track-slate-900">
          {channels.map((channel) => (
            <div
              key={channel._id}
              onClick={() => joinChannel(channel._id)}
              className={`p-5 cursor-pointer hover:bg-slate-700/50 border-l-4 border-transparent transition-all duration-300 hover:scale-[1.02] group hover:shadow-lg hover:shadow-purple-500/20 ${
                currentChannel === channel._id 
                  ? 'bg-gradient-to-r from-purple-500/20 to-pink-500/20 border-purple-400 shadow-lg scale-[1.02]' 
                  : ''
              } rounded-xl mx-2`}
            >
              <div className="flex items-center justify-between group-hover:translate-x-2 transition-transform duration-300">
                <span className="text-xl font-bold truncate">{`#${channel.name}`}</span>
                <div className="flex items-center space-x-3 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                  <span className="text-xs bg-slate-700/80 px-3 py-1 rounded-full font-mono">
                    {channel.members?.length || 0}
                  </span>
                  <div className="w-3 h-3 bg-green-400 rounded-full animate-ping" />
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col relative">
        {currentChannel ? (
          <>
            {/* Animated Header */}
            <div className="bg-slate-800/95 backdrop-blur-2xl border-b border-slate-700/50 p-6 flex items-center justify-between shadow-2xl sticky top-0 z-10">
              <div className="flex items-center space-x-4">
                <div className="w-14 h-14 bg-gradient-to-r from-purple-500 to-pink-500 rounded-2xl flex items-center justify-center shadow-2xl ring-2 ring-purple-500/30 animate-pulse">
                  <MessageCircle className="w-8 h-8" />
                </div>
                <div>
                  <h2 className="text-3xl font-black bg-gradient-to-r from-white via-slate-100 to-slate-200 bg-clip-text text-transparent drop-shadow-lg">
                    {channels.find(c => c._id === currentChannel)?.name || 'general'}
                  </h2>
                  <div className="flex items-center space-x-4 text-slate-400 mt-1">
                    <span className="flex items-center space-x-1">
                      <Users className="w-4 h-4" />
                      <span>{onlineUsers.length}</span>
                    </span>
                    {typingUsers.size > 0 && (
                      <span className="flex items-center space-x-1 text-sm bg-purple-500/20 px-3 py-1 rounded-full animate-pulse">
                        <div className="w-2 h-2 bg-purple-400 rounded-full animate-bounce" />
                        {Array.from(typingUsers).slice(0, 2).join(', ')} typing...
                      </span>
                    )}
                  </div>
                </div>
              </div>
              <button 
                onClick={() => setShowOnlineUsers(!showOnlineUsers)}
                className="p-4 bg-slate-700/50 hover:bg-slate-600/50 rounded-3xl transition-all duration-300 hover:scale-110 shadow-xl hover:shadow-purple-500/20 group"
              >
                <Users className="w-7 h-7 group-hover:scale-110 transition-transform" />
              </button>
            </div>

            {/* Messages Container */}
            <div className="flex-1 overflow-auto p-8 space-y-6 bg-slate-900/30 backdrop-blur-xl scrollbar-thin scrollbar-thumb-slate-600/50 scrollbar-track-transparent">
              {messages.map((message, idx) => (
                <div 
                  key={message._id || idx}
                  className={`group flex transition-all duration-300 hover:scale-[1.02] hover:shadow-2xl hover:shadow-purple-500/20 flex-col sm:flex-row sm:items-start animate-slide-in ${
                    message.sender?.id === user.id ? 'justify-end animate-slide-in-right' : 'justify-start animate-slide-in-left'
                  }`}
                >
                  <div className={`max-w-2xl p-6 rounded-3xl shadow-2xl backdrop-blur-xl border border-slate-700/30 transition-all duration-300 hover:shadow-purple-500/30 ${
                    message.sender?.id === user.id
                      ? 'bg-gradient-to-r from-purple-600/95 to-pink-600/95 text-white hover:from-purple-500 hover:to-pink-500'
                      : 'bg-white/10 hover:bg-white/20 text-white border-white/20'
                  }`}>
                    {/* âœ… FIXED HEADER - NO OVERLAP */}
                    <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3 mb-4">
                      {/* Username */}
                      <span className="font-black text-xl bg-gradient-to-r from-white to-slate-200 bg-clip-text text-transparent truncate max-w-[65%] sm:max-w-[50%]">
                        {message.sender?.name || 'Unknown'}
                      </span>
                      
                      {/* Time + Actions */}
                      <div className="flex items-center gap-4 flex-shrink-0">
                        <span className="text-sm font-mono bg-slate-800/60 backdrop-blur-sm px-3 py-1.5 rounded-xl opacity-90 whitespace-nowrap shadow-md">
                          {new Date(message.createdAt).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                        </span>
                        
                        {/* Reactions - Hover only */}
                        <div className="flex space-x-1 opacity-0 group-hover:opacity-100 transition-all duration-300 ml-auto sm:ml-0">
                          <button className="p-2 hover:bg-white/20 hover:scale-110 rounded-xl transition-all duration-200 shadow-md hover:shadow-yellow-500/25">
                            <ThumbsUp className="w-5 h-5 hover:text-yellow-400" />
                          </button>
                          <button 
                            onClick={() => editMessage(message)}
                            className="p-2 hover:bg-white/20 hover:scale-110 rounded-xl transition-all duration-200 shadow-md hover:shadow-blue-500/25"
                          >
                            <Edit3 className="w-5 h-5 hover:text-blue-400" />
                          </button>
                          <button 
                            onClick={() => deleteMessage(message._id)}
                            className="p-2 hover:bg-white/20 hover:scale-110 rounded-xl transition-all duration-200 shadow-md hover:shadow-red-500/25"
                          >
                            <Trash2 className="w-5 h-5 hover:text-red-400" />
                          </button>
                        </div>
                      </div>
                    </div>
                    
                    {/* Message Content */}
                    <p className="text-lg leading-relaxed break-words whitespace-pre-wrap max-w-full">
                      {message.text}
                    </p>
                  </div>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            {/* Interactive Input */}
            <div className="bg-slate-800/95 backdrop-blur-2xl border-t border-slate-700/50 p-8 shadow-2xl sticky bottom-0 z-20">
              <div className="relative group max-w-4xl mx-auto">
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && sendMessage()}
                  onFocus={handleTyping}
                  placeholder="Type your epic message here... ðŸš€"
                  className="w-full p-6 pl-16 pr-20 text-lg bg-white/5 hover:bg-white/10 border border-slate-600/50 rounded-3xl focus:ring-4 focus:ring-purple-500/40 focus:border-purple-500/60 focus:outline-none transition-all duration-300 placeholder-slate-400 text-white resize-none backdrop-blur-xl shadow-xl hover:shadow-2xl hover:shadow-purple-500/20 group-hover:scale-[1.02]"
                />
                <div className="absolute left-6 top-1/2 -translate-y-1/2 flex items-center space-x-3 opacity-0 group-hover:opacity-100 transition-all duration-400 transform translate-y-2 group-hover:translate-y-0">
                  <MessageCirclePlus className="w-6 h-6 text-slate-400 hover:text-purple-400 cursor-pointer p-2 hover:bg-purple-500/20 rounded-xl transition-all hover:rotate-12 hover:scale-110" />
                </div>
                <button
                  onClick={sendMessage}
                  disabled={!newMessage.trim()}
                  className="absolute right-4 top-1/2 -translate-y-1/2 p-4 bg-gradient-to-r from-purple-600 via-pink-600 to-purple-700 hover:from-purple-500 hover:via-pink-500 hover:to-purple-600 text-white rounded-3xl shadow-2xl hover:shadow-purple-500/50 transform hover:scale-110 hover:rotate-12 active:scale-95 transition-all duration-300 disabled:opacity-40 disabled:cursor-not-allowed disabled:shadow-none group"
                >
                  <Send className="w-7 h-7 group-hover:rotate-12 transition-transform duration-300" />
                </button>
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center p-20 bg-gradient-to-b from-slate-900/50 via-transparent to-slate-900/20">
            <div className="text-center space-y-8 animate-float-in">
              <div className="w-32 h-32 bg-gradient-to-r from-purple-500 via-pink-500 to-purple-600 rounded-3xl flex items-center justify-center mx-auto shadow-2xl ring-4 ring-purple-500/30 animate-spin-slow">
                <MessageCircle className="w-20 h-20 drop-shadow-lg" />
              </div>
              <div>
                <h3 className="text-5xl font-black bg-gradient-to-r from-white via-slate-100 to-purple-200 bg-clip-text text-transparent drop-shadow-2xl mb-4 tracking-tight">
                  Welcome to TeamChat
                </h3>
                <p className="text-2xl text-slate-400/80 backdrop-blur-sm px-8 py-4 bg-slate-800/30 rounded-2xl border border-slate-700/50">
                  Setting up your workspace...
                </p>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Online Users Panel */}
      {showOnlineUsers && (
        <div className="w-80 bg-slate-800/95 backdrop-blur-3xl border-l border-slate-700/50 shadow-2xl transform transition-all duration-500">
          <div className="p-8 border-b border-slate-700/50 sticky top-0 bg-slate-800/95 backdrop-blur-xl z-10">
            <h3 className="text-2xl font-black flex items-center gap-4 bg-gradient-to-r from-emerald-400 via-teal-400 to-emerald-500 bg-clip-text text-transparent drop-shadow-lg">
              <Users className="w-9 h-9 shadow-lg" />
              Live ({onlineUsers.length})
            </h3>
          </div>
          <div className="p-6 space-y-4 max-h-screen overflow-auto scrollbar-thin scrollbar-thumb-emerald-500/50 scrollbar-track-transparent">
            {onlineUsers.map((userId, idx) => (
              <div 
                key={userId || idx} 
                className="group flex items-center space-x-4 p-5 bg-slate-700/40 hover:bg-emerald-500/10 border border-slate-600/50 rounded-3xl hover:border-emerald-400/50 transition-all duration-300 hover:scale-105 hover:shadow-2xl hover:shadow-emerald-500/20"
              >
                <div className="w-16 h-16 bg-gradient-to-r from-emerald-500 to-teal-500 rounded-2xl flex items-center justify-center shadow-2xl ring-2 ring-emerald-400/40 animate-pulse">
                  <div className="w-8 h-8 bg-white/30 rounded-xl shadow-inner animate-ping" />
                </div>
                <div className="min-w-0 flex-1">
                  <span className="font-black text-xl truncate block group-hover:text-emerald-400 transition-colors">
                    {userId === user.id ? 'You' : `User ${idx + 1}`}
                  </span>
                  <p className="text-sm text-emerald-300/80 font-mono">Active now</p>
                </div>
                <div className="w-3 h-3 bg-emerald-400 rounded-full animate-ping shadow-lg" />
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
