import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../contexts/AuthContext';
import io from 'socket.io-client';
import axios from 'axios';
import { Users, MessageCircle, Plus, LogOut, Send, Edit3, Trash2, ThumbsUp } from 'lucide-react';
import toast from 'react-hot-toast';

const API_BASE = 'http://localhost:5000/api';
const SOCKET_URL = 'http://localhost:5000';

export default function ChatApp() {
  const { user, logout } = useAuth();
  const [channels, setChannels] = useState([]);
  const [currentChannel, setCurrentChannel] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [showOnlineUsers, setShowOnlineUsers] = useState(false);
  const [typingUsers, setTypingUsers] = useState(new Set());
  
  // üéØ PAGINATION STATE
  const [page, setPage] = useState(1);
  const [hasMoreMessages, setHasMoreMessages] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  
  const socketRef = useRef();
  const messagesEndRef = useRef(null);
  const observerRef = useRef(null);

  // Socket.IO + Auto-setup
  useEffect(() => {
    socketRef.current = io(SOCKET_URL, { withCredentials: true });

    socketRef.current.on('newMessage', (message) => {
      setMessages(prev => [...prev, message]);
    });

    socketRef.current.on('presence', (users) => setOnlineUsers(users));
    socketRef.current.on('typing', (data) => setTypingUsers(data.users));

    setupChat();

    return () => socketRef.current?.disconnect();
  }, []);

  // üéØ PAGINATION OBSERVER - Auto-load on scroll
  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && hasMoreMessages && !loadingMore) {
          loadMoreMessages();
        }
      },
      { threshold: 0.1 }
    );

    if (observerRef.current) {
      observer.observe(observerRef.current);
    }

    return () => observer.disconnect();
  }, [loadMoreMessages, hasMoreMessages, loadingMore]);

  const setupChat = async () => {
    try {
      const res = await axios.get(`${API_BASE}/channels`, { withCredentials: true });
      setChannels(res.data);

      if (res.data.length === 0) {
        await axios.post(`${API_BASE}/channels`, { name: 'general' }, { withCredentials: true });
        toast.success("General channel created! üéâ");
      }

      const channelsRes = await axios.get(`${API_BASE}/channels`, { withCredentials: true });
      const firstChannel = channelsRes.data[0];
      if (firstChannel) {
        await joinChannel(firstChannel._id);
      }
    } catch (error) {
      console.error('Setup failed:', error);
    }
  };

  // üéØ JOIN CHANNEL WITH PAGINATION (Page 1 only)
  const joinChannel = async (channelId) => {
    try {
      await axios.post(`${API_BASE}/channels/${channelId}/join`, {}, { withCredentials: true });
      setCurrentChannel(channelId);
      socketRef.current.emit('join', { userId: user.id, channelId });
      
      // Load PAGE 1 only (recent 30 messages)
      const res = await axios.get(`${API_BASE}/messages/${channelId}?page=1&limit=30`, { 
        withCredentials: true 
      });
      setMessages(res.data);
      setPage(1);
      setHasMoreMessages(res.data.length === 30); // More pages if exactly 30
      messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    } catch (error) {
      toast.error('Failed to join channel');
    }
  };

  // üéØ LOAD MORE MESSAGES (Infinite scroll)
  const loadMoreMessages = useCallback(async () => {
    if (!hasMoreMessages || loadingMore || !currentChannel) return;
    
    setLoadingMore(true);
    try {
      const nextPage = page + 1;
      const res = await axios.get(`${API_BASE}/messages/${currentChannel}?page=${nextPage}&limit=30`, { 
        withCredentials: true 
      });
      
      if (res.data.length > 0) {
        // PREPEND older messages to top
        setMessages(prev => [...res.data, ...prev]);
        setPage(nextPage);
        setHasMoreMessages(res.data.length === 30);
      } else {
        setHasMoreMessages(false);
      }
    } catch (error) {
      console.error('Load more failed:', error);
    } finally {
      setLoadingMore(false);
    }
  }, [currentChannel, page, hasMoreMessages, loadingMore]);

  const sendMessage = async () => {
    if (!newMessage.trim() || !currentChannel) return;

    try {
      const res = await axios.post(`${API_BASE}/messages`, {
        channelId: currentChannel,
        text: newMessage,
      }, { withCredentials: true });
      
      socketRef.current.emit('sendMessage', res.data);
      setNewMessage('');
    } catch (error) {
      toast.error('Failed to send message');
    }
  };

  const createChannel = async () => {
    const name = prompt('Channel name:');
    if (!name?.trim()) return;
    try {
      await axios.post(`${API_BASE}/channels`, { name: name.trim() }, { withCredentials: true });
      const res = await axios.get(`${API_BASE}/channels`, { withCredentials: true });
      setChannels(res.data);
      toast.success(`#${name.trim()} created! ‚ú®`);
    } catch {
      toast.error('Failed to create channel');
    }
  };

  return (
    <div className="h-screen flex bg-gradient-to-br from-slate-900 via-purple-900/20 to-slate-900 text-white overflow-hidden">
      {/* Sidebar */}
      <div className="w-80 bg-slate-800/90 backdrop-blur-xl border-r border-slate-700/50 flex flex-col shadow-2xl">
        <div className="p-6 border-b border-slate-700/50 flex items-center justify-between">
          <h1 className="text-2xl font-black bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent">
            TeamChat
          </h1>
          <div className="flex space-x-2">
            <button onClick={createChannel} className="p-3 hover:bg-purple-500/30 rounded-2xl hover:scale-110 transition-all">
              <Plus className="w-6 h-6" />
            </button>
            <button onClick={logout} className="p-3 hover:bg-red-500/30 rounded-2xl hover:scale-110 transition-all">
              <LogOut className="w-6 h-6" />
            </button>
          </div>
        </div>
        <div className="flex-1 overflow-auto py-4 space-y-2">
          {channels.map((channel) => (
            <div
              key={channel._id}
              onClick={() => joinChannel(channel._id)}
              className={`p-5 cursor-pointer hover:bg-slate-700/50 border-l-4 border-transparent transition-all hover:scale-[1.02] group rounded-xl mx-2 ${
                currentChannel === channel._id 
                  ? 'bg-gradient-to-r from-purple-500/20 to-pink-500/20 border-purple-400 shadow-lg scale-[1.02]' 
                  : ''
              }`}
            >
              <div className="flex items-center justify-between group-hover:translate-x-2 transition-transform">
                <span className="text-xl font-bold truncate">#{channel.name}</span>
                <span className="text-xs bg-slate-700/80 px-3 py-1 rounded-full font-mono">
                  {channel.members?.length || 0}
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Main Chat */}
      <div className="flex-1 flex flex-col">
        {currentChannel ? (
          <>
            {/* Header */}
            <div className="bg-slate-800/95 backdrop-blur-2xl border-b border-slate-700/50 p-6 flex items-center justify-between shadow-2xl">
              <div className="flex items-center space-x-4">
                <div className="w-14 h-14 bg-gradient-to-r from-purple-500 to-pink-500 rounded-2xl flex items-center justify-center shadow-2xl">
                  <MessageCircle className="w-8 h-8" />
                </div>
                <div>
                  <h2 className="text-3xl font-black bg-gradient-to-r from-white to-slate-200 bg-clip-text text-transparent">
                    {channels.find(c => c._id === currentChannel)?.name || 'general'}
                  </h2>
                  <p className="text-slate-400 flex items-center space-x-2">
                    <span>{onlineUsers.length} online</span>
                  </p>
                </div>
              </div>
              <button 
                onClick={() => setShowOnlineUsers(!showOnlineUsers)}
                className="p-4 bg-slate-700/50 hover:bg-slate-600/50 rounded-3xl transition-all hover:scale-110"
              >
                <Users className="w-7 h-7" />
              </button>
            </div>

            {/* üéØ PAGINATION MESSAGES CONTAINER */}
            <div className="flex-1 overflow-auto p-8 space-y-6 bg-slate-900/30 backdrop-blur-xl scrollbar-thin scrollbar-thumb-slate-600/50 scrollbar-track-transparent relative">
              
              {/* üéØ LOAD MORE TRIGGER */}
              <div ref={observerRef} className="w-full h-20 flex items-center justify-center mb-6 sticky top-0 z-10 bg-gradient-to-b from-slate-900/90 backdrop-blur-xl">
                {loadingMore && (
                  <div className="flex items-center space-x-3 bg-slate-800/80 p-4 rounded-2xl backdrop-blur-xl border border-slate-700/50 shadow-xl">
                    <div className="w-6 h-6 border-2 border-slate-500 border-t-purple-500 rounded-full animate-spin"></div>
                    <span className="text-slate-400 font-medium">Loading older messages...</span>
                  </div>
                )}
                {!loadingMore && !hasMoreMessages && messages.length > 0 && (
                  <div className="text-center text-slate-500/80 text-sm py-4 bg-slate-800/50 px-6 rounded-2xl backdrop-blur-sm border border-slate-700/30">
                    üèÅ All {messages.length} messages loaded
                  </div>
                )}
              </div>

              {/* Messages */}
              {messages.map((message, idx) => (
                <div 
                  key={message._id || idx}
                  className={`group flex transition-all duration-300 hover:scale-[1.02] hover:shadow-2xl flex-col sm:flex-row sm:items-start animate-slide-in ${
                    message.sender?.id === user.id ? 'justify-end' : 'justify-start'
                  }`}
                >
                  <div className={`max-w-2xl p-6 rounded-3xl shadow-2xl backdrop-blur-xl border border-slate-700/30 transition-all hover:shadow-purple-500/30 ${
                    message.sender?.id === user.id
                      ? 'bg-gradient-to-r from-purple-600/95 to-pink-600/95 text-white hover:from-purple-500 hover:to-pink-500'
                      : 'bg-white/10 hover:bg-white/20 text-white border-white/20'
                  }`}>
                    
                    {/* ‚úÖ FIXED HEADER - NO OVERLAP */}
                    <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3 mb-4">
                      <span className="font-black text-xl bg-gradient-to-r from-white to-slate-200 bg-clip-text text-transparent truncate max-w-[65%]">
                        {message.sender?.name || 'Unknown'}
                      </span>
                      
                      <div className="flex items-center gap-4 flex-shrink-0">
                        <span className="text-sm font-mono bg-slate-800/60 backdrop-blur-sm px-3 py-1.5 rounded-xl opacity-90 whitespace-nowrap shadow-md">
                          {new Date(message.createdAt).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                        </span>
                        
                        <div className="flex space-x-1 opacity-0 group-hover:opacity-100 transition-all duration-300 ml-auto sm:ml-0">
                          <button className="p-2 hover:bg-white/20 hover:scale-110 rounded-xl transition-all shadow-md">
                            <ThumbsUp className="w-5 h-5 hover:text-yellow-400" />
                          </button>
                          <button className="p-2 hover:bg-white/20 hover:scale-110 rounded-xl transition-all shadow-md">
                            <Edit3 className="w-5 h-5 hover:text-blue-400" />
                          </button>
                          <button className="p-2 hover:bg-white/20 hover:scale-110 rounded-xl transition-all shadow-md">
                            <Trash2 className="w-5 h-5 hover:text-red-400" />
                          </button>
                        </div>
                      </div>
                    </div>
                    
                    <p className="text-lg leading-relaxed break-words whitespace-pre-wrap">{message.text}</p>
                  </div>
                </div>
              ))}

              <div ref={messagesEndRef} />
            </div>

            {/* Input */}
            <div className="bg-slate-800/95 backdrop-blur-2xl border-t border-slate-700/50 p-8 shadow-2xl sticky bottom-0">
              <div className="relative max-w-4xl mx-auto">
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                  placeholder="Type a message..."
                  className="w-full p-6 pl-16 pr-20 text-lg bg-white/10 border border-slate-600 rounded-3xl focus:ring-4 focus:ring-purple-500/50 focus:border-transparent transition-all"
                />
                <button
                  onClick={sendMessage}
                  disabled={!newMessage.trim()}
                  className="absolute right-4 top-1/2 -translate-y-1/2 p-3 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white rounded-2xl shadow-lg hover:shadow-purple-500/25 transform hover:scale-110 transition-all disabled:opacity-50"
                >
                  <Send className="w-6 h-6" />
                </button>
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center p-20">
            <div className="text-center space-y-6">
              <MessageCircle className="w-24 h-24 text-slate-600 mx-auto animate-pulse" />
              <div>
                <h3 className="text-3xl font-bold text-slate-400 mb-2">Welcome to Team Chat</h3>
                <p className="text-slate-600">Loading channels...</p>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Online Users */}
      {showOnlineUsers && (
        <div className="w-80 bg-slate-800/95 backdrop-blur-2xl border-l border-slate-700 shadow-2xl">
          <div className="p-6 border-b border-slate-700">
            <h3 className="text-xl font-bold flex items-center gap-2">
              <Users className="w-6 h-6" />
              Online ({onlineUsers.length})
            </h3>
          </div>
          <div className="p-6 max-h-screen overflow-auto">
            {onlineUsers.map((userId, idx) => (
              <div key={userId || idx} className="flex items-center space-x-4 p-4 hover:bg-slate-700/50 rounded-xl mb-2">
                <div className="w-12 h-12 bg-green-500 rounded-xl flex items-center justify-center">
                  <div className="w-6 h-6 bg-white rounded-lg" />
                </div>
                <span className="font-semibold">{userId === user.id ? 'You' : `User ${idx + 1}`}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
