import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../contexts/AuthContext';
import io from 'socket.io-client';
import axios from 'axios';
import { Users, MessageCircle, Plus, LogOut, Send, Edit3, Trash2, ThumbsUp, MessageCirclePlus } from 'lucide-react';
import toast from 'react-hot-toast';

const API_BASE = 'http://localhost:5000/api';
const SOCKET_URL = 'http://localhost:5000';

export default function ChatApp() {
  const { user, logout } = useAuth();
  const [channels, setChannels] = useState([]);
  const [currentChannel, setCurrentChannel] = useState(null);

  const [messages, setMessages] = useState([]);

  // PAGINATION FIXED
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(false);
  const LIMIT = 50;

  const [newMessage, setNewMessage] = useState('');
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [showOnlineUsers, setShowOnlineUsers] = useState(false);
  const [typingUsers, setTypingUsers] = useState(new Set());
  const [editingMessage, setEditingMessage] = useState(null);

  const socketRef = useRef();
  const messagesContainerRef = useRef(null);
  const messagesEndRef = useRef(null);

  /* ---------------- SOCKET SETUP ---------------- */
  useEffect(() => {
    socketRef.current = io(SOCKET_URL, { withCredentials: true });

    socketRef.current.on('newMessage', (message) => {
      setMessages(prev => [...prev, message]);
      messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    });

    socketRef.current.on('presence', (users) => setOnlineUsers(users));

    socketRef.current.on('typing', (data) => setTypingUsers(data.users));

    setupChat();

    return () => socketRef.current?.disconnect();
  }, []);

  /* ---------------- INITIAL SETUP ---------------- */
  const setupChat = async () => {
    try {
      const res = await axios.get(`${API_BASE}/channels`, { withCredentials: true });
      setChannels(res.data);

      if (res.data.length === 0) {
        await axios.post(`${API_BASE}/channels`, { name: 'general' }, { withCredentials: true });
      }

      const channelsRes = await axios.get(`${API_BASE}/channels`, { withCredentials: true });
      const firstChannel = channelsRes.data[0];
      if (firstChannel) {
        await joinChannel(firstChannel._id);
      }
    } catch (error) {
      console.error('Setup failed:', error);
    }
  };

  /* ---------------- JOIN CHANNEL + LOAD FIRST PAGE ---------------- */
  const joinChannel = async (channelId) => {
    try {
      await axios.post(`${API_BASE}/channels/${channelId}/join`, {}, { withCredentials: true });

      setCurrentChannel(channelId);
      socketRef.current.emit('join', { userId: user.id, channelId });

      // reset pagination
      setPage(1);

      const res = await axios.get(
        `${API_BASE}/messages/${channelId}?page=1&limit=${LIMIT}`,
        { withCredentials: true }
      );

      setMessages(res.data);

      // SET hasMore CORRECTLY
      setHasMore(res.data.length === LIMIT);

      messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });

    } catch (error) {
      toast.error('Failed to join channel');
    }
  };

  /* ---------------- LOAD OLDER MESSAGES ---------------- */
  const loadMoreMessages = async () => {
    if (!hasMore) return;

    try {
      const nextPage = page + 1;

      const container = messagesContainerRef.current;
      const oldScrollHeight = container.scrollHeight;

      const res = await axios.get(
        `${API_BASE}/messages/${currentChannel}?page=${nextPage}&limit=${LIMIT}`,
        { withCredentials: true }
      );

      // NO MORE MESSAGES CASE
      if (res.data.length < LIMIT) {
        setHasMore(false);
      }

      if (res.data.length === 0) return;

      // PREPEND
      setMessages(prev => [...res.data, ...prev]);
      setPage(nextPage);

      // PREVENT SCROLL JUMP
      setTimeout(() => {
        container.scrollTop = container.scrollHeight - oldScrollHeight;
      }, 0);

    } catch (error) {
      console.error("Pagination error:", error);
    }
  };

  /* ---------------- DETECT SCROLL TOP FOR PAGINATION ---------------- */
  useEffect(() => {
    const container = messagesContainerRef.current;
    if (!container) return;

    const handleScroll = () => {
      if (container.scrollTop < 40 && hasMore) {
        loadMoreMessages();
      }
    };

    container.addEventListener('scroll', handleScroll);
    return () => container.removeEventListener('scroll', handleScroll);

  }, [hasMore, page]);

  /* ---------------- SEND MESSAGE ---------------- */
  const sendMessage = async () => {
    if (!newMessage.trim() || !currentChannel) return;

    try {
      const res = await axios.post(
        `${API_BASE}/messages`,
        { channelId: currentChannel, text: newMessage },
        { withCredentials: true }
      );

      socketRef.current.emit('sendMessage', res.data);
      setNewMessage('');

    } catch {
      toast.error('Failed to send message');
    }
  };

  const handleTyping = useCallback(() => {
    socketRef.current.emit('typing', { userId: user.id, channelId: currentChannel });
  }, [user.id, currentChannel]);

  const editMessage = (message) => {
    setEditingMessage(message);
    setNewMessage(message.text);
  };

  const saveEdit = async () => {
    setEditingMessage(null);
    setNewMessage('');
  };

  const deleteMessage = (messageId) => {
    toast.success('Message deleted!');
  };

  /* ---------------- UI ---------------- */
  return (
    <div className="h-screen flex bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 text-white overflow-hidden">

      {/* SIDEBAR */}
      <div className="w-80 bg-slate-800/90 backdrop-blur-xl border-r border-slate-700 flex flex-col shadow-2xl">
        <div className="p-6 border-b border-slate-700 flex items-center justify-between">
          <h1 className="text-2xl font-black bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent">
            TeamChat
          </h1>

          <div className="flex space-x-2">
            <button 
              onClick={() => {
                const name = prompt('Channel name:');
                if (name) axios.post(`${API_BASE}/channels`, { name }, { withCredentials: true });
              }}
              className="p-2 hover:bg-purple-500/20 rounded-xl"
            >
              <Plus className="w-6 h-6" />
            </button>

            <button 
              onClick={logout}
              className="p-2 hover:bg-red-500/20 rounded-xl"
            >
              <LogOut className="w-6 h-6" />
            </button>
          </div>
        </div>

        <div className="flex-1 overflow-auto py-4 space-y-1">
          {channels.map((channel) => (
            <div
              key={channel._id}
              onClick={() => joinChannel(channel._id)}
              className={`p-4 cursor-pointer hover:bg-slate-700/50 border-l-4 transition-all group ${
                currentChannel === channel._id 
                  ? 'bg-gradient-to-r from-purple-500/20 to-pink-500/20 border-purple-400 font-bold'
                  : 'border-transparent'
              }`}
            >
              <span className="text-lg font-semibold truncate">#{channel.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* MAIN CHAT */}
      <div className="flex-1 flex flex-col">
        {currentChannel ? (
          <>
            {/* HEADER */}
            <div className="bg-slate-800/90 border-b border-slate-700 p-6 flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-pink-500 rounded-2xl flex items-center justify-center">
                  <MessageCircle className="w-7 h-7" />
                </div>

                <div>
                  <h2 className="text-2xl font-black">general</h2>

                  <p className="text-slate-400">
                    {onlineUsers.length} online
                  </p>
                </div>
              </div>

              <button 
                onClick={() => setShowOnlineUsers(!showOnlineUsers)}
                className="p-3 hover:bg-slate-700/50 rounded-2xl"
              >
                <Users className="w-6 h-6" />
              </button>
            </div>

            {/* MESSAGES */}
            <div
              ref={messagesContainerRef}
              className="flex-1 overflow-auto p-8 space-y-4 bg-slate-900/20 backdrop-blur-xl"
            >
              {/* Show only when loading older pages */}
              {hasMore && page > 1 && (
                <p className="text-center text-slate-400 text-sm py-2">
                  Loading older messages...
                </p>
              )}

              {messages.map((message, idx) => (
                <div 
                  key={message._id || idx}
                  className={`flex ${
                    message.sender?.id === user.id ? 'justify-end' : 'justify-start'
                  }`}
                >
                  <div className={`max-w-md p-5 rounded-3xl border border-slate-700/50 ${
                    message.sender?.id === user.id
                      ? 'bg-gradient-to-r from-purple-600/90 to-pink-600/90'
                      : 'bg-white/10'
                  }`}>
                    <div className="flex items-start justify-between mb-2">
                      <span className="font-bold">{message.sender?.name}</span>
                      <span className="text-xs text-slate-300">
                        {new Date(message.createdAt).toLocaleTimeString([], {
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </span>
                    </div>

                    <p className="text-lg">{message.text}</p>
                  </div>
                </div>
              ))}

              <div ref={messagesEndRef} />
            </div>

            {/* INPUT */}
            <div className="bg-slate-800/90 border-t border-slate-700 p-6">
              <div className="relative group">
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                  onFocus={handleTyping}
                  placeholder="Type your message..."
                  className="w-full p-6 pl-16 pr-20 text-lg bg-white/10 border border-slate-600 rounded-3xl"
                />

                <button
                  onClick={sendMessage}
                  disabled={!newMessage.trim()}
                  className="absolute right-4 top-1/2 -translate-y-1/2 p-3 bg-gradient-to-r from-purple-600 to-pink-600 rounded-2xl"
                >
                  <Send className="w-6 h-6" />
                </button>
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center">Loading...</div>
        )}
      </div>

      {/* ONLINE USERS SIDEBAR */}
      {showOnlineUsers && (
        <div className="w-80 bg-slate-800/95 border-l border-slate-700 shadow-xl">
          <div className="p-6 border-b border-slate-700">
            <h3 className="text-xl font-black flex items-center gap-3">
              <Users className="w-7 h-7" /> Online ({onlineUsers.length})
            </h3>
          </div>

          <div className="p-6 space-y-3 overflow-auto">
            {onlineUsers.map((uid, idx) => (
              <div key={idx} className="p-4 bg-slate-700/50 rounded-2xl">
                <p>{uid === user.id ? 'You' : `User ${idx + 1}`}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
