import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../contexts/AuthContext';
import io from 'socket.io-client';
import axios from 'axios';
import { Users, MessageCircle, Plus, LogOut, ChevronLeft, ChevronRight, Send } from 'lucide-react';
import toast from 'react-hot-toast';

const API_BASE = 'http://localhost:5000/api';
const SOCKET_URL = 'http://localhost:5000';

export default function ChatApp() {
  const { user, logout } = useAuth();
  const [channels, setChannels] = useState([]);
  const [currentChannel, setCurrentChannel] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showOnlineUsers, setShowOnlineUsers] = useState(false);
  const socketRef = useRef();
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socketRef.current = io(SOCKET_URL);
    
    socketRef.current.on('newMessage', (message) => {
      setMessages(prev => [...prev, message]);
    });

    socketRef.current.on('presence', (users) => {
      setOnlineUsers(users);
    });

    fetchChannels();
    return () => socketRef.current.disconnect();
  }, []);

  const fetchChannels = async () => {
    try {
      const res = await axios.get(`${API_BASE}/channels`, { withCredentials: true });
      setChannels(res.data);
    } catch {
      toast.error('Failed to load channels');
    }
  };

  const createChannel = async () => {
    const name = prompt('Channel name:');
    if (!name) return;
    
    try {
      await axios.post(`${API_BASE}/channels`, { name }, { withCredentials: true });
      fetchChannels();
      toast.success('Channel created!');
    } catch {
      toast.error('Failed to create channel');
    }
  };

  const joinChannel = async (channelId) => {
    try {
      await axios.post(`${API_BASE}/channels/${channelId}/join`, {}, { withCredentials: true });
      setCurrentChannel(channelId);
      socketRef.current.emit('join', { userId: user.id, channelId });
      
      // Load messages
      const res = await axios.get(`${API_BASE}/messages/${channelId}?page=1&limit=50`, { 
        withCredentials: true 
      });
      setMessages(res.data);
      messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    } catch {
      toast.error('Failed to join channel');
    }
  };

  const sendMessage = async () => {
    if (!newMessage.trim() || !currentChannel) return;
    
    const message = {
      channel: currentChannel,
      text: newMessage,
      sender: { id: user.id, name: user.name }
    };
    
    try {
      const res = await axios.post(`${API_BASE}/messages`, message, { withCredentials: true });
      socketRef.current.emit('sendMessage', res.data);
      setNewMessage('');
      messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    } catch {
      toast.error('Failed to send message');
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="h-screen flex bg-gradient-to-br from-slate-50 to-slate-100 overflow-hidden">
      {/* Sidebar - Channels */}
      <div className="w-80 bg-white border-r border-gray-200 flex flex-col">
        <div className="p-6 border-b border-gray-200 flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <h1 className="text-xl font-bold text-gray-900">Channels</h1>
          </div>
          <div className="flex space-x-2">
            <button
              onClick={createChannel}
              className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <Plus className="w-5 h-5 text-gray-600" />
            </button>
            <button
              onClick={logout}
              className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <LogOut className="w-5 h-5 text-gray-600" />
            </button>
          </div>
        </div>

        <div className="flex-1 overflow-auto py-4">
          {channels.map((channel) => (
            <div
              key={channel._id}
              onClick={() => joinChannel(channel._id)}
              className={`p-4 cursor-pointer hover:bg-gray-50 border-l-4 border-transparent transition-all ${
                currentChannel === channel._id
                  ? 'bg-indigo-50 border-indigo-500 font-medium'
                  : ''
              }`}
            >
              <div className="flex items-center justify-between">
                <span className="text-gray-900 font-medium">#{channel.name}</span>
                <span className="text-xs bg-gray-200 px-2 py-1 rounded-full">
                  {channel.members?.length || 0}
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col">
        {currentChannel ? (
          <>
            {/* Chat Header */}
            <div className="bg-white border-b border-gray-200 p-4 flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-indigo-500 rounded-lg flex items-center justify-center">
                  <MessageCircle className="w-5 h-5 text-white" />
                </div>
                <div>
                  <h2 className="font-semibold text-gray-900"># general</h2>
                  <p className="text-sm text-gray-500">
                    {onlineUsers.length} online
                  </p>
                </div>
              </div>
              <button
                onClick={() => setShowOnlineUsers(!showOnlineUsers)}
                className="p-2 hover:bg-gray-100 rounded-lg"
              >
                <Users className="w-5 h-5 text-gray-600" />
              </button>
            </div>

            {/* Messages */}
            <div className="flex-1 overflow-auto p-6 bg-gradient-to-b from-white to-slate-50">
              {messages.map((message, idx) => (
                <div
                  key={message._id || idx}
                  className={`mb-4 flex ${
                    message.sender?.id === user.id ? 'justify-end' : 'justify-start'
                  }`}
                >
                  <div
                    className={`max-w-xs lg:max-w-md px-4 py-2 rounded-2xl shadow ${
                      message.sender?.id === user.id
                        ? 'bg-indigo-600 text-white rounded-br-sm'
                        : 'bg-white border'
                    }`}
                  >
                    <div className="flex items-baseline justify-between mb-1">
                      <span className="font-semibold text-sm">
                        {message.sender?.name || 'Unknown'}
                      </span>
                      <span className="text-xs opacity-75 ml-2">
                        {new Date(message.createdAt).toLocaleTimeString([], {
                          hour: '2-digit',
                          minute: '2-digit',
                        })}
                      </span>
                    </div>
                    <p className="text-sm leading-relaxed">{message.text}</p>
                  </div>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            {/* Message Input */}
            <div className="bg-white border-t border-gray-200 p-4">
              <div className="max-w-4xl mx-auto flex items-end space-x-3">
                <div className="flex-1 relative">
                  <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                    placeholder="Type a message..."
                    className="w-full p-4 pr-12 border border-gray-200 rounded-2xl focus:ring-2 focus:ring-indigo-500 focus:border-transparent resize-none"
                  />
                  <button
                    onClick={sendMessage}
                    disabled={!newMessage.trim()}
                    className="absolute right-3 bottom-3 p-1 rounded-full hover:bg-indigo-100 disabled:opacity-50"
                  >
                    <Send className="w-5 h-5 text-indigo-600" />
                  </button>
                </div>
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center p-12">
            <div className="text-center space-y-4">
              <MessageCircle className="w-16 h-16 text-gray-400 mx-auto" />
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">
                  Welcome to Team Chat
                </h3>
                <p className="text-gray-600">
                  Select a channel to start messaging
                </p>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Online Users Sidebar */}
      {showOnlineUsers && (
        <div className="w-64 bg-white border-l border-gray-200 shadow-lg">
          <div className="p-4 border-b border-gray-200">
            <h3 className="font-semibold text-gray-900 flex items-center gap-2">
              <Users className="w-4 h-4" />
              Online ({onlineUsers.length})
            </h3>
          </div>
          <div className="p-4">
            {onlineUsers.map((userId, idx) => (
              <div key={userId || idx} className="flex items-center space-x-3 p-2 hover:bg-gray-50 rounded-lg">
                <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center">
                  <div className="w-3 h-3 bg-white rounded-full" />
                </div>
                <span className="text-sm font-medium text-gray-900">
                  {userId === user.id ? 'You' : `User ${idx + 1}`}
                </span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
